import math
h = 0.1
N = int(10/h)


def b(n):
    if (n == 0) or (n == N):
        return -1
    return 2 + 10*h*h/12 * (10 + (n*h)**2)


def a(n):
    if n == N:
        return -1
    return 1 - h*h/12 * (10 + (n*h)**2)


def c(n):
    if n == 0:
        return 0
    return 1 - h*h/12 * (10 + (n*h)**2)


def d(n, i):
    if i == 0:
        if (n == 0) or (n == N):
            return 0
        return h*h * (n*h * math.exp(-h*n) *10/12 + ((n-1)*h*math.exp(-h*(n-1)) + (n+1)*h*math.exp(-h*(n+1)))/12)
    elif i == 1:
        if n == 0:
            return 1
        return 0
    else:
        if n == N:
            return 1
        return 0


def p(n, i):
    if n == 1:
        return c(0)/b(0)
    return c(n-1)/(b(n-1) - a(n-1)*p(n-1, i))


def q(n, i):
    if n == 1:
        return -d(0, i)/b(0)
    return (a(n-1)*q(n-1, i) - d(n-1, i))/(b(n-1) - a(n-1)*p(n-1, i))


def y(n, i):
    if n == N:
        return q(N+1, i)
    return p(n+1, i)*y(n+1, i) + q(n+1, i)


def y_f(i):
    return [y(n, i) for n in range(N)]


print(y_f(0))
print(y_f(1))
print(y_f(2))
